            if(t){
                return i;
            }
            if(i == m) break;
            int l = queries[i][0], r = queries[i][1], val = queries[i][2];
            for(int j = l; j <= r; j++){
                if(dp[j][nums[j]]) continue;
                for(int k = nums[j]; k >= val; k--){
                    dp[j][k] = dp[j][k] | dp[j][k - val];
                }
            }
        }
        return -1;
    }
};

