class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:

        def check(m):

            mem = defaultdict(list)

            for i in range(m):
                l,r,q = queries[i]
                for j in range(l, r + 1):
                    if q <= nums[j]:
                        mem[j].append(q)


            for i,v in enumerate(nums):
                if v == 0:
                    continue

                dic = Counter(mem[i])
                dp = {}
                dp[0] = 1
                items = []

                for key, val in dic.items():
                    cnt = 1

                    while val >= cnt:
                        val -= cnt
                        items.append((cnt * key))
                        cnt *= 2

                    if val:
                        items.append((val * key))

                for k in items:
                    tmp = deepcopy(dp)
                    for key in dp:
                        if key + k > v:
                            continue
                        if key + k == v:
                            tmp[v] = 1
                            break
                        tmp[key + k] = 1

                    dp = tmp

                if v not in dp:
                    # print(i, v, dic, dp, items)
                    return False


            return True










        leng = len(nums)
        if nums == [0] * leng:
            return 0

        l = 1
        r = len(queries)

        while l <= r:
            m = (l + r) // 2
            if check(m):
                r = m - 1
            else:
                l = m + 1

        return r + 1 if r + 1 <= len(queries) else -1

