            if (x == 0) {
                continue;
            }
            dp[0][x] = 1;
            auto as = nodes[i].a;
            int asiz = as.size();
            int res = -1;
            for (int j = 1; j <= asiz; j++) {
                int pos = as[j-1].first;
                int val = as[j-1].second;
                for (int k = x; k >= 0; k--) {
                    dp[j][k] |= dp[j-1][k];
                    if (k - val >= 0) {
                        dp[j][k-val] |= dp[j-1][k];
                    }
                }
                if (dp[j][0] == 1) {
                    res = pos+1;
                    break;
                }
            }
            if (res == -1) {
