class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(queries)
        @cache
        def dfs(tmp, i):
            nonlocal idx
            if tmp == 0:
                return i
            if i == n:
                return inf


            res = dfs(tmp, i+1)
            if queries[i][0] <= idx <= queries[i][1] and tmp >= queries[i][2]:
                res = min(res, dfs(tmp - queries[i][2], i+1))
            return res

        ans = 0
        for idx, x in enumerate(nums):
            need = dfs(x, 0)
            dfs.cache_clear()
            if need == inf:
                return -1
            ans = max(ans, need)

        return ans

