function minZeroArray(nums: number[], queries: number[][]): number {
  const n = nums.length;
  const p = new Array(n).fill(0).map(() => new Array(1001).fill(false));

  for (let i = 0; i < n; ++i) {
    p[i][nums[i]] = true;
  }

  if (p.every((v) => v[0])) {
    return 0;
  }

  for (let i = 0, l = queries.length; i < l; ++i) {
    const [a, b, v] = queries[i];

    for (let j = a; j <= b; ++j) {
      const pj = p[j];

      if (!pj[0]) {
        for (let k = 0; k <= 1000; ++k) {
          if (pj[k] && k >= v) {
            pj[k - v] = true;
          }
        }
      }
    }

    if (p.every((v) => v[0])) {
      return i + 1;
    }
  }

  return -1;
}

