func minZeroArray(nums []int, queries [][]int) int {
	if slices.Max(nums) == 0 {
		return 0
	}
	n := len(nums)
	ans := sort.Search(len(queries), func(i int) bool {
		cnt := make([][]int, n)
		for _, q := range queries[:i+1] {
			l, r, v := q[0], q[1], q[2]
			for j := l; j <= r; j++ {
				cnt[j] = append(cnt[j], v)
			}
		}
		tNums := make([]int, n)
		copy(tNums, nums)
		for j, tmp := range cnt {
			dp := make([]bool, nums[j]+1)
			dp[0] = true
			for _, v := range tmp {
				for w := nums[j]; w >= v; w-- {
					dp[w] = dp[w] || dp[w-v]
				}
			}
			if dp[nums[j]] {
				tNums[j] = 0
			} else {
				return false
			}
		}
		return true
	})

	if ans >= len(queries) {
		return -1
	}
	return ans + 1
}

