    int minZeroArray(vector<int>& a, vector<vector<int>>& qu) {
        int n = a.size();

        int ans = 0;
        for (int i = 0; i < n; ++i) {
            if (a[i] == 0) continue;
            vector<int> f(a[i] + 1);
            f[0] = 1;
            int aans = -1;
            for (int j = 0; j < qu.size(); ++j) {
                if (i < qu[j][0] || qu[j][1] < i || qu[j][2] > a[i])
                    continue;
                for (int k = a[i]; k >= qu[j][2]; --k)
                    f[k] |= f[k - qu[j][2]];
                if (f[a[i]]) {
                    aans = j;
                    break;
