class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n=nums.size(),m=queries.size(),ans=-1;
        set<int> now;
        vector<vector<int>> q(n+1);
        for(int i=0;i<m;++i){
            q[queries[i][0]].emplace_back(i);
            q[queries[i][1]+1].emplace_back(-i-1);
        }
        for(int i=0;i<n;++i){
            vector<int> dp(nums[i]+1, m);
            dp[0]=-1;
            for(auto &j:q[i]){
                if(j<0) now.erase(-j-1);
                else now.insert(j);
            }
            for(auto &j:now){
                for(int k=nums[i];k>=queries[j][2];--k){
                    if(dp[k-queries[j][2]]!=m) dp[k]=j;
                }
                if(dp[nums[i]]!=m) break;
            }
            if(dp[nums[i]]==m) return -1;
            ans=max(ans, dp[nums[i]]);
        }
        return ans+1;
    }
};

