class Solution {
    fun minZeroArray(nums: IntArray, queries: Array<IntArray>): Int {
        if (nums.all { it == 0 }) return 0

        val q = queries.size

        fun canZero(k: Int): Boolean {
            for (i in nums.indices) {
                val target = nums[i]
                val values = mutableListOf<Int>()
                for (j in 0 until k) {
                    val (l, r, v) = queries[j]
                    if (i in l..r) {
                        values.add(v)
                    }
                }
                val dp = BooleanArray(target + 1)
                dp[0] = true
                for (v in values) {
                    for (s in target downTo v) {
                        if (dp[s - v]) dp[s] = true
                    }
                }
                if (!dp[target]) return false
            }
            return true
        }

        var lo = 1
        var hi = q + 1
        var ans = -1
        while (lo < hi) {
            val mid = lo + (hi - lo) / 2
            if (canZero(mid)) {
                ans = mid
                hi = mid
            } else {
                lo = mid + 1
            }
        }
        return if (ans == -1) -1 else ans
    }
}

