public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n=nums.size(),m=queries.size(),ma=*max_element(nums.begin(),nums.end());

        auto check=[&](int x){
            vector f(n,vector(ma+1,0));
            for(int i=0;i<n;++i){
                f[i][nums[i]]=1;
            }
            for(int i=0;i<x;++i){
                int l=queries[i][0],r=queries[i][1],val=queries[i][2];
                for(int j=l;j<=r;++j){
                    for(int k=val;k<=nums[j];++k){
                        f[j][k-val]|=f[j][k];
                    }
                }
            }
            int ans=0;
            for(int i=0;i<n;++i) ans+=f[i][0];
            return ans==n;
        };

        int l=0,r=m;
        while(l<r){
            int mid=l+r>>1;
            if(check(mid)) r=mid;
            else l=mid+1;
        }
        if(check(l)) return l;
        else return -1;
    }
};

