class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int ans = 0;
        for (int i = 0; i < nums.length; i++) {
            List<int[]> list = new ArrayList<>();
            for (int j = 0; j < queries.length; j++) {
                int[] q = queries[j];
                if (q[0] <= i && q[1] >= i) {
                    list.add(new int[]{q[2], j + 1});
                }
            }
            ans = Math.max(ans, getNs(list, nums[i]));
        }
        return ans == Integer.MAX_VALUE ? -1 : ans;
    }

    private int getNs(List<int[]> list, int num) {
        if(num==0) return 0;
        int sz = list.size();
        boolean[][] nn = new boolean[sz + 1][num + 1];
        nn[0][0] = true;
        for (int i = 0; i < sz; i++) {
            for (int j = 0; j <= num; j++) {
                if (!nn[i][j]) continue;
                if (j + list.get(i)[0] <= num) {
                    nn[i + 1][j + list.get(i)[0]] = true;
                }
                nn[i + 1][j] = true;
            }
        }
        for (int i = 1; i <= sz; i++) {
            if (nn[i][num]) return list.get(i - 1)[1];
        }
        return Integer.MAX_VALUE;
    }
}

