        n = len(nums)
        subs = [set() for _ in range(n)]
        chk = [False for _ in range(n)]
        rm = n
        for i in range(n):
            subs[i].add(nums[i])
            if nums[i] == 0:
                chk[i] = True
                rm -= 1
        if rm == 0:
            return 0
        for i, q in enumerate(queries):
            for j in range(q[0], q[1] + 1):
                if chk[j]:
                    continue
                ns = set()
                for x in subs[j]:
                    ns.add(x)
                    if x >= q[2]:
                        ns.add(x - q[2])
                subs[j] = ns
                if 0 in ns:
                    chk[j] = True
                    rm -= 1
            if rm == 0:
                return i + 1
        return -1

