const g = (idx, v, queries) => {
    const dp = new Array(v + 1).fill(Infinity)
    dp[v] = 0
    for (let i = 0; i < queries.length; i += 1) {
        const [l, r, d] = queries[i]
        if (l > idx || r < idx) continue
        for (let j = 0; j <= v - d; j += 1) {
            dp[j] = Math.min(dp[j], dp[j + d] === Infinity ? Infinity : i + 1)
        }
    }
    return dp[0]
}
/**
 * @param {number[]} nums
 * @param {number[][]} queries
 * @return {number}
 */
var minZeroArray = function(nums, queries) {
    const arr = nums.map((v, i) => g(i, v, queries))
    const ans = Math.max(...arr)
    return ans === Infinity ? -1 : ans
};

