class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        int m = queries.length;
        int res = 0;

        int [][]dp = new int[n+1][1001];
        for(int i=0;i<n;++i){
            dp[i][0] = 1;
            if(nums[i]==0){
                continue;
            }
            for(int j=0;j<m;++j){
                if(!(queries[j][0]<=i && queries[j][1]>=i)){
                    continue;
                }
                int val = queries[j][2];
                for(int k=nums[i];k>=val;--k){
                    if(dp[i][k-val]==1){
                        dp[i][k] = 1;
                    }
                }
                if(dp[i][nums[i]]!=0){
                    res = Math.max(res, j+1);
                    break;
                }
            }
            if(dp[i][nums[i]] == 0){
                return -1;
            }
        }
        return res;
    }
}

