        dic = defaultdict(list)
        for j, (l, r, v) in enumerate(queries):
            for i in range(l, r + 1):
                dic[i].append((v, j))
        ans = 0
        for i in range(n):
            target = nums[i]
            if not target: continue
            if not dic[i]: return -1
            v = dic[i]
            seen = set([0])
            for a, j in v:
                if target - a in seen:
                    tmp = j
                    break
                ss = set()
                for b in seen:
                    if b + a < target:
                        ss.add(b + a)
                seen |= ss
            else:
                return -1
            ans = max(ans, tmp + 1)
        return ans

