import java.util.*;

class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        int m = queries.length;
        int maxK = -1;

        for (int j = 0; j < n; j++) {
            int target = nums[j];
            if (target == 0) {
                continue;
            }

            List<Integer> covers = new ArrayList<>();
            for (int i = 0; i < m; i++) {
                int l = queries[i][0];
                int r = queries[i][1];
                if (l <= j && j <= r) {
                    covers.add(i);
                }
            }

            Map<Integer, Integer> dp = new HashMap<>();
            dp.put(0, -1);
            for (int i : covers) {
                int val = queries[i][2];
                Map<Integer, Integer> newDp = new HashMap<>(dp);
                for (Map.Entry<Integer, Integer> entry : dp.entrySet()) {
                    int sum = entry.getKey();
                    int currentMax = entry.getValue();
                    int newSum = sum + val;
                    if (newSum > target) {
                        continue;
                    }
                    int newMax = Math.max(currentMax, i);
                    if (!newDp.containsKey(newSum) || newMax < newDp.get(newSum)) {
                        newDp.put(newSum, newMax);
                    }
                }
                dp = newDp;
            }

            if (!dp.containsKey(target)) {
                return -1;
            }
            int currentMax = dp.get(target);
            maxK = Math.max(maxK, currentMax);
        }

        if (maxK == -1) {
            for (int num : nums) {
                if (num != 0) {
                    return -1;
                }
            }
            return 0;
        }

        return maxK + 1;
    }
}

