class Solution {
   public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        vector<int> ans(nums.size(), -1);
        for (int i = 0; i < nums.size(); i++) {
            int x = nums[i];
            vector<bool> f(x + 1), g(x + 1);
            f[0] = true;
            for (int q = 0; q < queries.size(); q++) {
                int l = queries[q][0], r = queries[q][1], v = queries[q][2];
                if (l <= i && i <= r) {
                    for (int j = 0; j <= x; j++) {
                        g[j] = f[j];
                        if (j - v >= 0) {
                            g[j] = g[j] | f[j - v];
                        }
                    }
                    swap(f, g);
                    if (f.back()) {
                        ans[i] = q + 1;
                        break;
                    }
                }
            }
            if (x == 0) ans[i] = 0;
        }
        int mx = 0;
        for (int i: ans) {
            if (i == -1) return -1;
            mx = max(mx, i);
        }
        return mx;
    }
};

