class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int cnt = 0;
        int max = Arrays.stream(nums).max().getAsInt();
        int n = nums.length;

        boolean[][] dp = new boolean[n][max + 10];
        for (int i = 0; i < n; i++) {
            dp[i][0] = true;
        }
        for (int j = 0; j < n; j++) {
            if (dp[j][nums[j]]) {
                cnt++;
            }
        }
        if (cnt == n) {
            return 0;
        }
        for (int i = 0; i < queries.length; i++) {
            int l = queries[i][0];
            int r = queries[i][1];
            int v = queries[i][2];
            for (int j = l; j <= r; j++) {
                for (int target = nums[j]; target >= v; target--) {
                    dp[j][target] |= dp[j][target - v];
                }
            }
            cnt = 0;
            for (int j = 0; j < n; j++) {
                if (dp[j][nums[j]]) {
                    cnt++;
                }
            }
            if (cnt == n) {
                return i + 1;
            }
        }
        return -1;
    }
}

