class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& q) {
        int n = nums.size();

        vector<pair<int,int>> v[n];
        for(int i = 0; i < q.size(); ++i) {
            int l = q[i][0], r = q[i][1];
            for(int j = l; j <= r; ++j) {
                v[j].push_back({q[i][2], i});
            }
        }

        int ret = 0;

        for(int i = 0; i < n; ++i) {
            if(nums[i] == 0) continue;
            int f[2][nums[i] + 1];
            memset(f, 0, sizeof(f));
            f[0][0] = 1;
            int res = -1;
            for(int j = 0, cur = 0, ne = 1; j < v[i].size(); ++j) {
                for(int k = 0; k < nums[i]; ++k) {
                    if(f[cur][k]) {
                        f[ne][k] = 1;
                        if(k + v[i][j].first <= nums[i]) {
                            f[ne][k + v[i][j].first] = 1;
                        }
                    }
                }
                if(f[ne][nums[i]]) {
                    res = v[i][j].second;
                    break;
                }
                cur ^= 1;
                ne ^= 1;
            }
            if(res == -1) return -1;
            ret = max(ret, res + 1);
        }

        return ret;
    }
};

