        dp[i][0] = 1;
        if (nums[i] == 0) cnt--;
    }
    if (cnt == 0) return 0;
    int ans = 0;
    for (int k = 0;k < q;k++) {
        ans = k + 1;
        int w = queries[k][2];
        for (int i = queries[k][0]; i <= queries[k][1];i++) {
            if (dp[i][nums[i]] == 1) continue;
            for (int j = nums[i]; j - w >= 0;j--) {
