class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        List<Integer>[] vals = new ArrayList[n];
        int[] valsSum = new int[n];
        Arrays.setAll(vals, i -> new ArrayList<>());

        int k = 0;
        boolean[] valid = new boolean[n];
        int sum = 0;
        for (int i = 0; i < n; i++) {
            sum += nums[i];
            if (nums[i] == 0) valid[i] = true;
        }

        if (sum == 0) return 0;

        for (; k < queries.length; k++) {
            int l = queries[k][0], r = queries[k][1];
            for (int i = l; i <= r; i++) {
                if (!valid[i]) {
                    vals[i].add(queries[k][2]);
                    valsSum[i] += queries[k][2];
                    if (valsSum[i] < nums[i]) continue;
                    if (valsSum[i] == nums[i] || check(nums[i], vals[i])) {
                        valid[i] = true;
                        sum -= nums[i];
                    }
                }
            }

            if (sum == 0) return k + 1;
        }

        return -1;
    }

    boolean check(int x, List<Integer> vals) {
        return dfs(x, vals, 0);
    }

    boolean dfs(int x, List<Integer> vals, int i) {
        if (x == 0) return true;
        if (i == vals.size() || x < 0) return false;

        boolean ans = false;
        for (int j = i; j < vals.size() && !ans; j++) {
            ans |= dfs(x - vals.get(j), vals, j+1);
        }

        return ans;
    }
}

