        res = -1
        for i in range(n):
            if nums[i] == 0:
                continue
            vis = []
            for idx, (l, r, val) in enumerate(queries):
                if l <= i <= r:
                    vis.append((idx, val))
            vis.sort()
            dp = {0: -1}
            for (idx, val) in vis:
                for s, cur_max in list(dp.items()):
                    new_s = s + val
                    new_max = max(cur_max, idx)
                    if new_s > nums[i]:
                        continue
                    if new_s not in dp or new_max < dp[new_s]:
                        dp[new_s] = new_max
            if nums[i] not in dp:
                return -1
            res = max(res, dp[nums[i]])
        return res + 1

