func minZeroArray(nums []int, queries [][]int) int {
	inf := int(1e18)
	ans := 0

	calc := func(i, x int) int {
		f := make([]bool, x+1)
		f[0] = true
		for j, q := range queries {
			l, r, val := q[0], q[1], q[2]
			if i > r || i < l || x-val < 0 {
				continue
			}
			if f[x-val] {
				return j + 1
			}
			for k := x; k >= val; k-- {
				if f[k-val] {
					f[k] = true
				}
			}
		}
		return inf
	}

	for i, x := range nums {
		if x == 0 {
			continue
		}
		ans = max(ans, calc(i, x))
	}

	if ans == inf {
		return -1
	}
	return ans
}

