class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        # diff
        n = len(nums)
        def www(arr,y):
            @cache
            def dfs(i,y):
                if y==0:
                    return True
                if i>=len(arr) or y<0:
                    return False if y!=0 else True
                return dfs(i+1,y) or dfs(i+1,y-arr[i])
            return dfs(0,y)
        def check(x):
            ans = nums.copy()
            dic = {}
            for i,j,d in queries[:x]:
                for x in range(i,j+1):
                    if x in dic:
                        dic[x].append(d)
                    else:
                        dic[x] = [d]
            for i,y in enumerate(ans):
                # x->0
                if y>0:
                    t = dic.get(i,[])
                    if t==None:
                        return False
                    if not www(t,y):
                        return False
            return True
        l = 0
        r = len(queries)
        res = -1
        while l<=r:
            mid = (l+r)//2
            if check(mid):
                res = mid
                r = mid-1
            else:
                l = mid+1
        return res

