class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n=len(nums)
        mp={}
        cnt=0
        a=[False]*n
        for i in range(n):
            mp[i]={nums[i]:True}
            if nums[i]==0:
                a[i]=True
                cnt+=1
        if cnt==n:return 0
        for i in range(len(queries)):
            l,r,val=queries[i][0],queries[i][1],queries[i][2]
            for j in range(l,r+1):
                if a[j]:continue
                if val in mp[j]:
                    a[j]=True
                    cnt+=1
                else:
                    temp=set()
                    for key in mp[j].keys():
                        if key-val<0:continue
                        temp.add(key-val)
                    for s in temp:
                        mp[j][s]=True
            if cnt==n:return i+1
        return -1

