class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int len = queries.size();
        unordered_set<int> vis;
        int dp[len+1][n][1001];
        memset(dp, 0, sizeof(dp));
        for(int i=0;i<n;i++) {
            dp[0][i][nums[i]] = 1;
            if(nums[i] == 0) vis.insert(i);
        }

        if(vis.size() == n) return 0;


        for(int i=1;i<=len;i++) {
            int l = queries[i-1][0], r = queries[i-1][1], v = queries[i-1][2];
            for(int j=0;j<n;j++) {
                for(int k=0;k<=1000;k++) {
                    dp[i][j][k] = dp[i-1][j][k];
                }
            }
            for(int j=l;j<=r;j++) {
                for(int k=v;k<=1000;k++) {
                    if(dp[i-1][j][k]) {
                        dp[i][j][k-v] = 1;
                        if(k-v == 0) vis.insert(j);
                        if(vis.size() == n) return i;
                    }
                }
            }
        }

        return -1;
    }
};

