class Solution {
int n, m;
int dp[1005][1005];
public:

    int dfs(int pos, int x, int index, vector<vector<int>>& q)
{
    if(x == 0) return pos;
    if(pos == m || x < 0) return 0x3f3f3f3f;
    if(dp[pos][x] != -1) return dp[pos][x];
    int ans = dfs(pos + 1, x, index, q);
    if(q[pos][0] <= index && q[pos][1] >= index)
        ans = min(dfs(pos + 1, x - q[pos][2], index, q), ans);
    dp[pos][x] = ans;
    return ans;
}

    int minZeroArray(vector<int>& nums, vector<vector<int>>& q) {
        n = nums.size(), m = q.size();
        int ans = 0;
        for(int i = 0; i < n; i++)
            {
                if(nums[i] == 0) continue;
                for(int j = 0; j < m; j++)
                    for(int k = 0; k <= 1000; k++)
                        dp[j][k] = -1;
                int t = dfs(0, nums[i], i, q);
                // cout << t << " ";
                ans = max(ans, t);
            }
        if(ans == 0x3f3f3f3f) return -1;
        return ans;
    }
};

