            for (int[] q : queries) {
                ++p;
                if (q[0] <= i && i <= q[1]) {
                    for (int j = 0; j + q[2] <= nums[i]; ++j) {
                        dp[j] |= dp[j + q[2]];
                    }
                }
                if (dp[0]) break;
            }
            if (!dp[0]) return -1;
            res = Math.max(res, p);
        }
        return res;
    }
}

