func minZeroArray(nums []int, queries [][]int) int {
	n := len(nums)
	sum := 0
	for _, num := range nums {
		sum += num
	}
	if sum == 0 {
		return 0
	}
	mpList := []map[int]bool{}
	for i := 0; i < n; i++ {
		mpList = append(mpList, map[int]bool{nums[i]: true})
	}
	for i, q := range queries {
		l, r, v := q[0], q[1], q[2]
		for j := l; j <= r; j++ {
			mp := mpList[j]
			tmp := map[int]bool{}
			for num := range mp {
				tmp[num] = true
				if num >= v {
					tmp[num-v] = true
				}
			}
			mpList[j] = tmp
		}
		if AllZero(mpList) {
			return i + 1
		}
	}
	return -1
}

func AllZero(mpList []map[int]bool) bool {
	for _, mp := range mpList {
		if !mp[0] {
			return false
		}
	}
	return true
}

