class Solution {
public:
    int sum(vector<int>& arr) {
        int n = 0;
        for (auto& i : arr) {
            n += i;
        }
        return n;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        if (sum(nums) == 0) {
            return 0;
        }
        vector<int> arr(nums.size());
        vector<vector<int>> arr2(nums.size());
        for (int i = 0; i < arr2.size(); i++) {
            arr2[i] = vector<int>(nums[i] + 1);
            arr2[i][0] = 1;
            if (arr2[i].back())
                arr[i] = true;
        }
        int ans = 0;
        while (ans != queries.size()) {
            int left = queries[ans][0];
            int right = queries[ans][1];
            for (int i = left; i <= right; i++) {
                if (arr[i])
                    continue;
                for (int j = arr2[i].size() - 1; j >= 0; j--) {
                    if (arr2[i][j] && j + queries[ans][2] < arr2[i].size()) {
                        arr2[i][j + queries[ans][2]] = 1;
                        if (arr2[i].back()) {
                            arr[i] = 1;
                            break;
                        }
                    }
                }
            }
            if (sum(arr) == nums.size())
                break;
            ans++;
        }
        if (ans == queries.size()) {
            return -1;
        }
        return ans + 1;
    }
};

