                if (q[j][0] > i || q[j][1] < i) {
                    j++;
                    continue;
                }
                for (int k = a[i]; k >= q[j][2]; k--) {
                    f[k] |= f[k - q[j][2]];
                }
                if (f[a[i]]) {
                    break;
                }
                // std::cout << j << "n";
                j++;
            }
            if (j == m) {
                return -1;
            }
            ans = std::max(ans, j + 1);
        }
        return ans;
    }
};

