class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries)
    {
    	int  n = nums.size();
    	int  m = queries.size();
    	int  N = *max_element(nums.begin(), nums.end());
    	auto check = [&](int mid) -> bool {
    		vector<vector<int>> f(n, vector<int>(N + 1, 0));
    		for (int i = 0; i < n; i++) f[i][0] = 1;
    		for (int k = 0; k < mid; k++)
    		{
    			for (int i = queries[k][0]; i <= queries[k][1]; i++)
    			{
    				for (int j = nums[i]; j >= queries[k][2]; j--)
    				{
    					f[i][j] |= f[i][j - queries[k][2]];
    				}
    			}
    		}
    		for (int i = 0; i < n; i++)
    			if (!f[i][nums[i]]) return false;
    		return true;
    	};

    	int l = 1, r = m;
    	while (l <= r)
    	{
    		int mid = (l + r) >> 1;
    		if (check(mid))
    			r = mid - 1;
    		else
    			l = mid + 1;
    	}
        if(check(0))return 0;
    	return l == m + 1 ? -1 : l;
    }
};

