class Solution {
public:
    bool solve(vector<int>& summand, int target)
    {
        vector<int> dp(target+1);
        dp[0] = 1;
        for(int x: summand)
        {
            for(int y = target; y >= x; --y)
            {
                if(dp[y - x])
                {
                    dp[y] = 1;
                }
            }
        }
        return dp[target];
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int q = queries.size();
        int l = 0;
        int r = q;
        int ans = -1;
        while(l <= r)
        {
            int md = (l+r)/2;
            vector<vector<int>> add(n);
            for(int i = 0; i < md; ++i)
            {
                for(int j = queries[i][0]; j <= queries[i][1]; ++j)
                {
                    add[j].push_back(queries[i][2]);        
                }
            }
            bool ok = true;
            for(int i = 0; i < n; ++i)
            {
                if(!solve(add[i], nums[i]))
                {
                    ok = false;
                    break;
                }
            }
            if(ok) 
            {
                ans = md;
                r = md - 1;
            }
            else
            {
                l = md + 1;
            }
        }
        return ans;
    }
};

