class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), m = queries.size(), res = 0;
        int l, r, x, y;
        vector<int> d(n, -1);
        for(int k = 0; k < n; ++k){
            y = nums[k];
            vector<bool> dp(1001);
            dp[0] = true;
            if(y == 0){
                d[k] = 0;
                continue;
            }
            for(int i = 0; i < m; ++i){
                l = queries[i][0];
                r = queries[i][1];
                x = queries[i][2];
                if(k < l || k > r){
                    continue;
                }
                for(int j = y; j >= x; --j){
                    if(dp[j - x]){
                        dp[j] = true;
                    }
                }
                if(dp[y]){
                    // cout<<k<<" "<<y<<" "<<i<<endl;
                    d[k] = i + 1;
                    break;
                }
            }
            if(d[k] == -1){
                res = -1; break;
            }
            res = max(res, d[k]);
        }
        return res;
    }
};

