class Solution {
public:
     int coinChange(vector<int> &nums, int target) {
        if (target == 0) return true;
        if (nums.size() == 0 && target != 0) return false;

        vector<bool> dp(target + 1, false);
        dp[0] = true;

        for (int num : nums) {
            if (num <= target) {
                for (int j = target; j >= num; --j) {
                    if (dp[j - num]) {
                        dp[j] = true;
                    }
                }
            }
        }
        return dp[target];
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& q) {
        int n = nums.size();
        vector<vector<int>> c;
        c.resize(n);
        bool f = true;
        for (int i = 0; i < n; i++) {
            if (nums[i] != 0) break;
            if (i == n - 1) return 0;
        }
        auto check = [&] () -> bool {
            for (int i = 0; i < n; i++) {
                if (!coinChange(c[i], nums[i])) return false;
            }
            return true;
        };
        for (int i = 0; i < q.size(); i++) {
           int l = q[i][0], r = q[i][1], v = q[i][2];
            for (int j = l; j <= r; j++) {
                c[j].push_back(v);
            }
            if (check()) return i +1;
        }
        return -1;
    }
};

