class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n=nums.size(),nq=queries.size(),iq=0;
        int mask=(1<<n)-1;
        int maxv=*max_element(nums.begin(),nums.end());
        if(maxv==0) return 0;
        vector<bool> vt(maxv+1,false);
        vt[0]=true;
        vector<vector<bool>> m(n,vt);
        for(;iq<nq && mask!=0;++iq)
        {
            for(int i=queries[iq][0];i<=queries[iq][1];++i)
            {
                if((mask&(1<<i))==0) continue;
                vector<bool> &last=m[i];
                for(int val=queries[iq][2],j=nums[i]-val;j>=0;--j)
                {
                    if(last[j]) last[j+val]=true;
                }
                if(last[nums[i]]) mask^=(1<<i);
            }
        }
        if(mask!=0) iq=-1;
        return iq;
    }
};

