class Solution:

    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:

        if all(i == 0 for i in nums):
            return 0

        n = len(nums)
        d = defaultdict(list)
        for qi, (l, r, v) in enumerate(queries):
            t = [v, qi]
            for i in range(l, r + 1):
                d[i].append(t)

        def check(ns, tar):

            if tar == 0:
                return -1

            cur_vs = set()
            cur_vs.add(0)
            for v, qi in ns:
                to_add = set()
                for cur in cur_vs:
                    t = cur + v
                    if t == tar:
                        return qi
                    to_add.add(t)
                cur_vs |= to_add
                # print("after check v: {}, qi: {}, the cur_vs is {}".format(v, qi, cur_vs))

            return None

        ans = list()
        for i in range(n):
            # print("d[i]: {}, nums[i]: {}".format(d[i], nums[i]))
            qi = check(d[i], nums[i])
            if qi is None:
                return -1
            ans.append(qi)

        return max(ans) + 1

