class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length, res = 0;
        Set<Integer> s = new HashSet<>();
        for (int i = 0; i < n; i++) {
            if (nums[i] != 0) {
                s.add(i);
            }
        }
        if (s.size() == 0) return 0;
        Map<Integer, Set<Integer>> m = new HashMap<>();
        for (int i = 0; i < queries.length; i++) {
            int[] q = queries[i];
            for (int j = q[0]; j <= q[1]; j++) {
                if (!s.contains(j)) continue;
                m.putIfAbsent(j, new HashSet<>());
                Set<Integer> se = m.get(j);
                HashSet<Integer> te = new HashSet<>();
                if (!se.isEmpty()) {
                    for (Integer it : se) {
                        if (q[2] + it == nums[j]) s.remove(j);
                        te.add(q[2] + it);
                    }
                }
                te.add(q[2]);
                se.addAll(te);

                if (q[2] == nums[j]) {
                    s.remove(j);
                }
            }
            // System.out.println(m);
            if (s.size() == 0) return i + 1;
        }
        return -1;
    }
}

