class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        int m = queries.length;
        boolean isOk = true;
        for (int num : nums) {
            if (num != 0) {
                isOk = false;
                break;
            }
        }
        if (isOk) return 0;
        for (int k = 1; k <= m ; k++) {
            boolean ok = true;
            for (int i = 0 ; i < n ; i++){
                int t = nums[i];
                List<Integer> list = new ArrayList<>();
                for (int j = 0; j < k; j++) {
                    int l = queries[j][0];
                    int r = queries[j][1];
                    int val = queries[j][2];
                    if (l <= i && i <= r){
                        list.add(val);
                    }
                }
                if (!check(list,t)){
                    ok = false;
                    break;
                }
            }
            if (ok) return k;
        }
        return -1;
    }

    private boolean check(List<Integer> list,int capacity){
        boolean[] f = new boolean[capacity + 1];
        f[0] = true;
        for (Integer x : list) {
            for (int i = capacity; i >= x ; i--) {
                f[i] = f[i] || f[i - x];
            }
        }
        return f[capacity];
    }
}

