class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        const int N = nums.size();
        const int Q = queries.size();
        int ret = 0;
        for (int i = 0; i < N; i++) {
            if (nums[i] == 0) {
                continue;
            }
            set<int> cur;
            cur.insert(nums[i]);
            int k = -1;
            for (int j = 0; j < Q; j++) {
                auto l = queries[j][0];
                auto r = queries[j][1];
                auto val = queries[j][2];

                if (l > i || r < i) {
                    continue;
                }

                if (cur.find(val) != cur.end()) {
                    k = j;
                    break;
                }

                set<int> next;
                for (auto x : cur) {
                    if (x > val) {
                        next.insert(x - val);
                    }
                }
                for (auto x : next) {
                    cur.insert(x);
                }
            }
            if (k == -1) {
                return -1;
            } else {
                ret = max(ret, k + 1);
            }
        }
        return ret == INT_MAX ? -1 : ret;
    }
};

