class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        auto getMinK = [&](int i)->int {
            if (nums[i] == 0) return 0;
            bitset<1001> can;
            can.reset();
            can[nums[i]] = 1;
            for (int k = 1; k <= queries.size(); k++) {
                int iL = queries[k - 1][0], iR = queries[k - 1][1], val = queries[k - 1][2];
                if ((iL <= i && i <= iR)) can |= (can >> val);
                if (can[0]) return k;
            }
            return queries.size() + 1;
        };
        int ans = 0;
        for (int i = 0; i < nums.size(); i++) {
            ans = max(ans, getMinK(i));
        }
        return ans == (queries.size() + 1) ? -1 : ans;
    }

};

