class Solution:  
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:  
        if all(v == 0 for v in nums):  
            return 0  
        n = len(nums)  
        l, r = 0, len(queries)  
        ans = -1  

        def check(k: int) -> bool:  
            for i in range(n):  
                v = [q[2] for q in queries[:k] if q[0] <= i <= q[1]]  
                target = nums[i]  
                dp = [False] * (target + 1)  
                dp[0] = True  

                for val in v:   
                    for j in range(target, -1, -1):  
                        if dp[j] and j + val <= target:  
                            dp[j + val] = True  
                    if dp[target]:  
                        break  
                if not dp[target]:  
                    return False  
            return True  

        while l <= r:  
            mid = (l + r) >> 1  
            if check(mid):  
                ans = mid  
                r = mid - 1  
            else:  
                l = mid + 1  

        return ans

