/**
 * @param {number[]} nums
 * @param {number[][]} queries
 * @return {number}
 */
var minZeroArray = function(nums, queries) {
        function helper(index, q_i, t_c, map) {
        if (q_i >= queries.length) return Infinity;
        const s = q_i + ',' + t_c;
        if (map.has(s)) return map.get(s);
        const [l, r, val] = queries[q_i];
        let rt = Infinity;
        if (l <= index && r >= index && t_c >= val) {
            const next_c = t_c - val;
            if (next_c === 0) {
                rt = q_i;
            } else {
                const next = helper(index, q_i + 1, next_c, map);
                rt = next;
            }
        }
        const next_2 = helper(index, q_i + 1, t_c, map);
        rt = Math.min(rt, next_2);
        map.set(s, rt);
        return rt;
    }
    const c = nums.reduce((sum, v) => sum + v, 0);
    if (c === 0) return 0;
    let rt = -1;
    for (let i = 0; i < nums.length; i++) {
        if (nums[i] === 0) continue;
        const map = new Map();
        let t_i = helper(i, 0, nums[i], map);
        if (t_i === Infinity) {
            return -1;
        } else {
            t_i += 1;
        }
        rt = Math.max(t_i, rt);
    }
    return rt;
};

