class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& q) {
        int n=nums.size();
        auto dfs=[&](auto &dfs,int i,int x,int y)->int {
            if(x==0) {
                return i;
            }
            if(i>=q.size()) return INT32_MAX;
            if(dp[i][x]!=-1) return dp[i][x];
            int res=dfs(dfs,i+1,x,y);
            if(q[i][0]<=y&&q[i][1]>=y) {
                if(x>=q[i][2]) res=min(res,dfs(dfs,i+1,x-q[i][2],y));
            }
            dp[i][x]=res;
            return res;
        };
        int res=0;
        for(int y=0;y<n;y++) {
            int x=nums[y];
            for(int i=0;i<q.size();i++) {
                for(int j=0;j<=x;j++) {
                    dp[i][j]=-1;
                }
            }
            res=max(res,dfs(dfs,0,x,y));
        }
        if(res==INT32_MAX) return -1;
        return res;
    }
};

