class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        mx = max(nums)
        if mx == 0:
            return 0

        n = len(nums)
        arr = [[False]*(mx+1) for v in nums]
        for i in range(n):
            arr[i][nums[i]] = True

        left = sum([1 if v > 0 else 0 for v in nums])

        for idx,(l,r,v) in enumerate(queries):
            for i in range(l,r+1):
                if arr[i][0]:
                    continue
                for e in range(1,mx+1):
                    if not arr[i][e]:
                        continue
                    if e == v:
                        arr[i][0] = True
                        left -= 1
                        break
                    if e > v:
                        arr[i][e-v] = True
            #print(idx,(l,r,v),'left',left,'arr',arr)
            if not left:
                return idx+1
        return -1

