class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        List<Integer>[] a = new List[n];
        Arrays.setAll(a, k -> new ArrayList<>());
        if (check(nums, a)) {
            return 0;
        }
        int ans = 0;
        for (int[] q : queries) {
            for (int i = q[0]; i <= q[1]; ++i) {
                a[i].add(q[2]);
            }
            ans++;
            if (check(nums, a)) {
                return ans;
            }
        }
        return -1;
    }
    boolean check(int[] a, List<Integer>[] li) {
        int n = a.length;
        for (int i = 0; i < n; ++i) {
            boolean[] f = new boolean[a[i] + 1];
            f[0] = true;
            for (int x : li[i]) {
                for (int j = a[i]; j >= x; --j) {
                    f[j] |= f[j - x];
                }
            }
            if (!f[a[i]]) {
                return false;
            }
        }
        return true;
    }
}

