class Solution {
public:
    const int INF=1003;
    int f(vector<int>& a,int t){
        if(t==0)return 0;
        int n=a.size();
        vector<vector<bool>> dp(n,vector<bool>(t+1,0));
        dp[0][0]=true;
        dp[0][a[0]]=true;
        for(int i=1;i<n;i++){
            for(int j=0;j<=t;j++){
                if(dp[i-1][j])dp[i][j]=true;
                else if(j>=a[i]){
                    dp[i][j]=dp[i-1][j-a[i]];
                }
            }
        }
        for(int i=0;i<n;i++){
            if(dp[i][t]){
                return i+1;
            }
        }
        return INF;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& q) {
        int n=q.size(),m=nums.size();
        vector<vector<int>> a(m,vector<int>(n,0));
        for(int i=0;i<n;i++){
            for(int j=q[i][0];j<=q[i][1];j++){
                a[j][i]=q[i][2];
            }
        }
        vector<int> ans;
        for(int i=0;i<m;i++){
            ans.push_back(f(a[i],nums[i]));
        }
        int ans0=*max_element(ans.begin(),ans.end());
        return ans0==INF?-1:ans0;
    }
};

