class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n=len(nums)
        m=len(queries)
        q=[[] for _ in range(n)]
        for j,(l,r,val) in enumerate(queries):
            for i in range(l,r+1):
                if 0<=i<n:
                    q[i].append((j,val))
        d=[inf]*n
        for i in range(n):
            x=nums[i]
            if x==0:
                d[i]=-1
                continue
            h=q[i]
            if not h:
                return -1
            dp={0:-1}
            for j,val in h:
                cur=dict(dp)
                for s,mx in cur.items():
                    nsum=s+val
                    if nsum>x:
                        continue
                    nmx=max(mx,j)
                    if nsum not in dp or dp[nsum]>nmx:
                        dp[nsum]=nmx
            if x in dp:
                d[i]=dp[x]
        return max(d)+1 if max(d)<inf else -1

