            cnt++;
            if(site >= query[0] && site <= query[1]){//
                for(int i=target; i>=0;i--){
                    if(i - query[2] >= 0 && dp[i - query[2]] == 1){
                        dp[i] = 1;
                    }
                }
            }
            if(dp[target] == 1) return cnt;
        }
        return -1;
    }
};

