func minZeroArray(nums []int, queries [][]int) int {
    n := len(queries)
    k := 0
    for i, c := range nums {
        if c == 0 {
            continue
        }
        tmp := make(map[int]int)
        tmp[0] = 1
        cached := false
        for idx := 0; idx < n; idx++ {
            if i < queries[idx][0] || i > queries[idx][1] {
                continue
            }
            if _, ok := tmp[c-queries[idx][2]]; ok {
                cached = true
                k = max(k, idx+1)
                break
            }
            cc := make(map[int]int)
            for v, _ := range tmp {
                cc[v] = 1
                if v+queries[idx][2] <= c {
                    cc[v+queries[idx][2]] = 1
                }
            }
            tmp = cc
        }
        if !cached {
            return -1
        }
    }

    return k
}

